'use client';

import React, { useState } from 'react';

const FormFile = ({ field, onChange, error }: any) => {
    const [uploading, setUploading] = useState(false);
    const [fileName, setFileName] = useState('');

    const handleFileChange = async (e: React.ChangeEvent<HTMLInputElement>) => {
        const file = e.target.files?.[0];
        if (!file) return;

        setUploading(true);
        setFileName(file.name);

        const formData = new FormData();
        formData.append('file', file);

        try {
            // Gunakan upload_url dari definisi field, fallback ke /api/upload jika tidak ada
            const response = await fetch(field.upload_url || '/api/upload', {
                method: 'POST',
                body: formData,
            });

            if (!response.ok) {
                throw new Error('Upload failed');
            }

            const result = await response.json();
            // Panggil onChange dengan path atau ID file dari response backend
            onChange({ target: { name: field.name, value: result.filePath } }); // Sesuaikan `result.filePath` dengan response API Anda

        } catch (err) {
            console.error(err);
            alert('File upload failed.');
            setFileName('');
        } finally {
            setUploading(false);
        }
    };

    return (
         <div className={field.field_class_name || 'col-span-1'}>
            <label htmlFor={field.name} className="block text-sm font-medium text-gray-700">{field.label}</label>
            <input
                type="file"
                id={field.name}
                name={field.name}
                onChange={handleFileChange}
                disabled={uploading}
                className="mt-1 block w-full text-sm text-gray-500 file:mr-4 file:py-2 file:px-4 file:rounded-full file:border-0 file:text-sm file:font-semibold file:bg-violet-50 file:text-violet-700 hover:file:bg-violet-100"
            />
            {uploading && <p className="text-sm text-gray-500 mt-1">Uploading: {fileName}...</p>}
            {error && <p className="text-red-500 text-xs mt-1">{error}</p>}
            {field.helperText && <p className="text-gray-500 text-xs mt-1">{field.helperText}</p>}
        </div>
    );
};

export default FormFile;

