'use client';

import React, { useState, useEffect } from 'react';

const FormSelect = ({ field, value, formData, onChange, error }: any) => {
    const [options, setOptions] = useState<{ value: string; label: string }[]>([]);
    const [loading, setLoading] = useState(false);

    const parentValue = field.depends_on_field ? formData[field.depends_on_field] : null;

    useEffect(() => {
        // Jika ada source_url, fetch data dari API
        if (field.source_url) {
            // Jika ini adalah dropdown bertingkat tapi parent-nya belum dipilih, jangan fetch data.
            if (field.depends_on_field && !parentValue) {
                setOptions([]);
                return;
            }

            const fetchOptions = async () => {
                setLoading(true);
                let url = field.source_url;

                // Jika ada dependensi, tambahkan query parameter ke URL
                if (field.depends_on_field && field.depends_on_param && parentValue) {
                    url += `?${field.depends_on_param}=${parentValue}`;
                }

                try {
                    const response = await fetch(url);
                    if (!response.ok) throw new Error('Network response was not ok');
                    const data = await response.json();

                    const mappedOptions = data.map((item: any) => ({
                        value: item[field.source_value_field || 'id'],
                        label: item[field.source_label_field || 'name']
                    }));
                    setOptions(mappedOptions);
                } catch (e) {
                    console.error("Failed to fetch select options from URL:", e);
                    setOptions([]);
                } finally {
                    setLoading(false);
                }
            };

            fetchOptions();
        } else if (field.options) {
            // Jika menggunakan options hardcoded
            setOptions(field.options.split('\n').map((opt: string) => ({ value: opt, label: opt })));
        }
    }, [field.source_url, field.options, field.source_value_field, field.source_label_field, parentValue, field.depends_on_field, field.depends_on_param]);

    // Setiap kali parentValue berubah, reset nilai dropdown ini
    useEffect(() => {
        if (field.depends_on_field && parentValue) {
            // Kita panggil onChange dari parent untuk mereset nilainya di state utama
            onChange({ target: { name: field.name, value: '' } });
        }
    }, [parentValue]);

    return (
        <div className={field.field_class_name || 'col-span-1'}>
            <label htmlFor={field.name} className="block text-sm font-medium text-gray-700">{field.label}</label>
            <select
                id={field.name}
                name={field.name}
                value={value}
                onChange={onChange}
                disabled={loading || (field.depends_on_field && !parentValue)}
                className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm"
            >
                <option value="">{loading ? 'Memuat...' : (field.placeholder || 'Pilih salah satu')}</option>
                {options.map((opt, index) => (
                    <option key={index} value={opt.value}>{opt.label}</option>
                ))}
            </select>
            {error && <p className="text-red-500 text-xs mt-1">{error}</p>}
            {field.helperText && <p className="text-gray-500 text-xs mt-1">{field.helperText}</p>}
        </div>
    );
};

export default FormSelect;

