'use client';

import React from 'react';
import FormInput from './FormInput'; // Asumsi repeater hanya berisi text input untuk saat ini
// Anda bisa mengimpor komponen lain jika repeater perlu merender tipe field yang lebih beragam

const FormRepeater = ({ field, value, onChange, error }: any) => {
    // Pastikan value adalah array
    const items = Array.isArray(value) ? value : [];

    const handleAddItem = () => {
        // Buat item baru berdasarkan repeater_fields dari definisi
        const newItem = field.repeater_fields.reduce((acc: any, f: any) => ({ ...acc, [f.name]: '' }), {});
        onChange({ target: { name: field.name, value: [...items, newItem] } });
    };

    const handleRemoveItem = (index: number) => {
        const newValue = items.filter((_: any, i: number) => i !== index);
        onChange({ target: { name: field.name, value: newValue } });
    };

    const handleItemChange = (index: number, e: React.ChangeEvent<HTMLInputElement>) => {
        const { name, value: itemValue } = e.target;
        const newItems = [...items];
        newItems[index] = { ...newItems[index], [name]: itemValue };
        onChange({ target: { name: field.name, value: newItems } });
    };

    return (
        <div className={`${field.field_class_name || 'col-span-1'} p-4 border rounded-md mb-4`}>
            <label className="block text-lg font-medium text-gray-900 mb-2">{field.label}</label>
            {field.helperText && <p className="text-gray-500 text-sm mb-4">{field.helperText}</p>}

            {items.map((item: any, index: number) => (
                <div key={index} className="p-3 border rounded-md mb-3 relative">
                    <h4 className="font-semibold mb-2">Item #{index + 1}</h4>
                    <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                        {field.repeater_fields.map((subField: any) => {
                            // Di sini Anda bisa menambahkan switch case jika sub-field bisa berbagai tipe
                            return (
                                <FormInput
                                    key={subField.name}
                                    field={subField}
                                    value={item[subField.name] || ''}
                                    onChange={(e: any) => handleItemChange(index, e)}
                                    // error={...} // Penanganan error bisa ditambahkan
                                />
                            );
                        })}
                    </div>
                    <button
                        type="button"
                        onClick={() => handleRemoveItem(index)}
                        className="absolute top-2 right-2 text-red-500 hover:text-red-700"
                    >
                        &times;
                    </button>
                </div>
            ))}
            <button
                type="button"
                onClick={handleAddItem}
                className="mt-2 text-sm text-blue-600 hover:text-blue-800"
            >
                + {field.addActionLabel || 'Tambah Item'}
            </button>
            {error && typeof error === 'string' && <p className="text-red-500 text-xs mt-1">{error}</p>}
        </div>
    );
};

export default FormRepeater;

// src/app/components/form/GenerateFormFields.tsx
'use client';

import React from 'react';
import FormInput from './FormInput';
import FormTextarea from './FormTextarea';
import FormSelect from './FormSelect';
import FormCheckbox from './FormCheckbox';
import FormFile from './FormFile';
import FormRepeater from './FormRepeater';

interface GenerateFormFieldsProps {
    formFields: any[];
    formData: any;
    handleInputChange: (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement | HTMLSelectElement>) => void;
    formErrors: any;
}

const GenerateFormFields: React.FC<GenerateFormFieldsProps> = ({
    formFields,
    formData,
    handleInputChange,
    formErrors,
}) => {

    // Fungsi untuk merender field individual
    const renderField = (field: any) => {
        const value = formData[field.name];
        const error = formErrors[field.name];

        // Logika untuk menampilkan/menyembunyikan field berdasarkan checkbox
        // Ini adalah implementasi dari efek 'toggle_visibility'
        let isVisible = true;
        // Cari field lain (checkbox) yang mungkin mengontrol visibilitas field ini
        const controllerField = formFields.find(f => f.toggle_target_field === field.name);
        if (controllerField) {
            const controllerValue = formData[controllerField.name]; // Nilai checkbox (true/false)
            if (controllerField.toggle_effect === 'show_if_checked') {
                isVisible = !!controllerValue;
            } else if (controllerField.toggle_effect === 'hide_if_checked') {
                isVisible = !controllerValue;
            }
        }
        if (!isVisible) {
            return null; // Jangan render field ini
        }

        switch (field.type) {
            case 'textarea':
                return <FormTextarea field={field} value={value || ''} onChange={handleInputChange} error={error} />;
            case 'select':
                return <FormSelect field={field} value={value || ''} formData={formData} onChange={handleInputChange} error={error} />;
            case 'checkbox':
                return <FormCheckbox field={field} value={value || false} onChange={handleInputChange} error={error} />;
            case 'file':
                return <FormFile field={field} onChange={handleInputChange} error={error} />;
            case 'repeater':
                // Untuk repeater, kita perlu handler khusus karena valuenya adalah array of objects
                const handleRepeaterChange = (e: { target: { name: string, value: any } }) => {
                    const { name, value } = e.target;
                    // Ini adalah cara sederhana, di komponen utama (page.tsx) kita akan
                    // menyatukannya ke dalam satu handleInputChange yang lebih cerdas
                    handleInputChange(e as any);
                };
                return <FormRepeater field={field} value={value || []} onChange={handleRepeaterChange} error={error} />;
            case 'text':
            case 'email':
            case 'tel':
            default:
                return <FormInput field={field} value={value || ''} onChange={handleInputChange} error={error} />;
        }
    };

    return (
        <div className={`grid ${formData.form_class_name || 'grid-cols-1'} gap-6`}>
            {formFields.map(field => (
                <div key={field.name}>
                    {renderField(field)}
                </div>
            ))}
        </div>
    );
};

export default GenerateFormFields;

