const mockPrograms: Program[] = [
    { id: 1, program_name: 'Program A' },
    { id: 2, program_name: 'Program B' },
];

const mockPackages: { [key: number]: Package[] } = {
    1: [
        {
            id: 101, name: 'Paket Basic A', price: 100000, discount: 0, description: 'Deskripsi paket basic',
            package_features: [{ feature_name: 'Fitur 1' }, { feature_name: 'Fitur 2' }],
            form_fields: [
