                { name: 'main_author_phone', label: 'Telepon Penulis Utama', type: 'tel', rules: 'required', field_class_name: 'col-span-1' },
                {
                    name: 'include_main_author', label: 'Jadikan Penulis Utama sebagai Anggota #1', type: 'checkbox',
                    on_change_effect: 'copy_to_repeater',
                    copy_source_fields: 'main_author_name,main_author_phone',
                    copy_target_repeater: 'members',
                    copy_target_repeater_fields: 'name,phone',
                    field_class_name: 'col-span-2'
                },
                {
                    name: 'members', label: 'Daftar Anggota Lain', type: 'repeater', addActionLabel: 'Tambah Anggota',
                    repeater_fields: [
                        { name: 'name', label: 'Nama Anggota', type: 'text', rules: 'required' },
                        { name: 'phone', label: 'Telepon Anggota', type: 'tel', rules: 'required' }
                    ],
                    field_class_name: 'col-span-2'
                }
            ]
        },
    ],
};


const PackPage: React.FC = () => {
    const [programs, setPrograms] = useState<Program[]>([]);
    const [selectedProgram, setSelectedProgram] = useState<Program | null>(null);
    const [packages, setPackages] = useState<Package[]>([]);
    const [selectedPackage, setSelectedPackage] = useState<Package | null>(null);
    const [loading, setLoading] = useState(true);
    const [formData, setFormData] = useState<any>({});
    const [formErrors, setFormErrors] = useState<any>({});

    // Inisialisasi state awal
    useEffect(() => {
        // Ganti dengan fetch API asli
        setPrograms(mockPrograms);
        if (mockPrograms.length > 0) {
            setSelectedProgram(mockPrograms[0]);
        }
        setLoading(false);
    }, []);

    // Fetch paket saat program dipilih
    useEffect(() => {
        if (!selectedProgram) return;
        setLoading(true);
        // Ganti dengan fetch API asli
        const newPackages = mockPackages[selectedProgram.id] || [];
        setPackages(newPackages);
        setSelectedPackage(null); // Reset pilihan paket
        setLoading(false);
    }, [selectedProgram]);

    // Inisialisasi formData saat paket dipilih
    useEffect(() => {
        if (!selectedPackage) {
            setFormData({});
            return;
        }
        const initialData: any = {};
        selectedPackage.form_fields.forEach(field => {
            if (field.type === 'repeater') {
                initialData[field.name] = [];
            } else if (field.type === 'checkbox') {
                initialData[field.name] = false;
            } else {
                initialData[field.name] = '';
            }
        });
        setFormData(initialData);
        setFormErrors({});
    }, [selectedPackage]);

    // EFEK DINAMIS: Menangani interaksi antar field
    useEffect(() => {
        if (!selectedPackage || Object.keys(formData).length === 0) return;

        let dataChanged = false;
        const newFormData = { ...formData };

        selectedPackage.form_fields.forEach(field => {
            // 1. Efek 'copy_to_repeater'
            if (field.type === 'checkbox' && field.on_change_effect === 'copy_to_repeater') {
                const isChecked = formData[field.name];
                const sources = field.copy_source_fields.split(',');
                const targetRepeater = field.copy_target_repeater;
                const targetFields = field.copy_target_repeater_fields.split(',');
                const mainAuthorId = `main_author_${field.name}`; // ID unik

                const sourceValues = sources.map(source => formData[source]);
                const hasAllSourceValues = sourceValues.every(val => val);

                const currentRepeaterValue = Array.isArray(newFormData[targetRepeater]) ? [...newFormData[targetRepeater]] : [];
                const itemIndex = currentRepeaterValue.findIndex(item => item._id === mainAuthorId);

                if (isChecked && hasAllSourceValues && itemIndex === -1) {
                    const newItem: any = { _id: mainAuthorId };
                    sources.forEach((source, index) => {
                        newItem[targetFields[index]] = formData[source];
                    });
                    currentRepeaterValue.unshift(newItem); // Tambah ke awal
                    newFormData[targetRepeater] = currentRepeaterValue;
                    dataChanged = true;
                } else if (!isChecked && itemIndex !== -1) {
                    newFormData[targetRepeater] = currentRepeaterValue.filter(item => item._id !== mainAuthorId);
                    dataChanged = true;
                }
            }
        });

        if (dataChanged) {
            setFormData(newFormData);
        }
    }, [formData, selectedPackage]);


    const handleInputChange = (e: React.ChangeEvent<any>) => {
        const { name, value, type, checked } = e.target;
        setFormData((prev: any) => ({
            ...prev,
            [name]: type === 'checkbox' ? checked : value,
        }));
    };

    const handleFormSubmit = async (e: React.FormEvent) => {
        e.preventDefault();
        if (!selectedPackage) return;
        console.log('Submitting form data:', formData);
        // Logika submit ke API backend Anda di sini
        alert('Data disubmit! (lihat konsol)');
    };

    if (loading && programs.length === 0) return <div>Loading...</div>;

    return (
        <div className="container mx-auto p-4">
            <h1 className="text-3xl font-bold mb-4">Pilih Program & Paket</h1>

            {/* ... UI untuk memilih program dan paket ... */}
            {/* (Saya akan singkat bagian ini agar fokus ke form) */}
            <div className="mb-8">
                <h2 className="text-2xl font-semibold mb-2">Program</h2>
                <div className="flex space-x-2">
                    {programs.map(p => <button key={p.id} onClick={() => setSelectedProgram(p)} className={`px-4 py-2 rounded ${selectedProgram?.id === p.id ? 'bg-blue-500 text-white' : 'bg-gray-200'}`}>{p.program_name}</button>)}
                </div>
            </div>
            {selectedProgram && (
                <div className="mb-8">
                    <h2 className="text-2xl font-semibold mb-2">Paket untuk {selectedProgram.program_name}</h2>
                    {packages.map(pkg => (
                        <div key={pkg.id} onClick={() => setSelectedPackage(pkg)} className={`p-4 border rounded cursor-pointer ${selectedPackage?.id === pkg.id ? 'border-blue-500' : ''}`}>
                            <h3 className="font-bold">{pkg.name}</h3>
                            <p>{pkg.description}</p>
                        </div>
                    ))}
                </div>
            )}


            {/* Dynamic Form */}
            {selectedPackage && (
                <form onSubmit={handleFormSubmit}>
                    <h2 className="text-2xl font-semibold mb-4">Formulir Registrasi untuk {selectedPackage.name}</h2>
                    <GenerateFormFields
                        formFields={selectedPackage.form_fields}
                        formData={formData}
                        handleInputChange={handleInputChange}
                        formErrors={formErrors}
                    />
                    <button type="submit" className="mt-6 w-full bg-green-500 text-white py-2 px-4 rounded">
                        Kirim Pendaftaran
                    </button>
                </form>
            )}
        </div>
    );
};

